pragma solidity ^0.4.20;

import "./SafeMath.sol";

contract Owned {
    address private candidate;
    address public owner;

    mapping(address => bool) public admins;

    function Owned() public 
    {
        owner = msg.sender;
    }

    function changeOwner(address newOwner) public 
    {
        require(msg.sender == owner);
        candidate = newOwner;
    }

    function confirmOwner() public 
    {
        require(candidate == msg.sender); // run by name=candidate
        owner = candidate;
    }

    function addAdmin(address addr) external 
    {
        require(msg.sender == owner);
        admins[addr] = true;
    }

    function removeAdmin(address addr) external
    {
        require(msg.sender == owner);
        admins[addr] = false;
    }
}

contract BettingGame is Owned {
    using SafeMath for uint;

    enum Result { HOMEWIN, AWAYWIN, DRAW, UNKNOWN }
    enum BetContent { HOMEWIN, HOMENOTWIN, AWAYWIN, AWAYNOTWIN, DRAW, NOTDRAW }

    struct Game {
        uint id;
        string home;
        string away;
        uint startTime;
        bool ended;
        bool isFreezing;
        Result result;
        string details;
    }

    struct Bet {
        address sender;
        uint stake;
        BetContent content;
        uint odd;
        uint id;
        uint matched;
        bool cancelled;
        uint result;
        address referrer;
    }

    Game public game;
    uint betID;

    Bet[] public placedBets;
    mapping(uint8 => mapping(uint => uint[])) oddPools;
    mapping(uint8 => uint[]) public allOdds;
    mapping(uint8 => mapping(uint => uint)) public liquidities;
    mapping(address => uint[]) public userBets;
    mapping(address => uint) public pendingWithdrawals;

    event GameEndEvent(string details);
    event BetPlaceEvent(address user);
    event BetMatchEvent(uint id);
    event BetCancelEvent(uint id);
    event LiquidityChangeEvent(uint8 betContent, uint odd, uint liquidity);

    modifier onlyOwner {
        require(msg.sender == owner);
        _;
    }

    modifier onlyAdmin {
        require(msg.sender == owner || admins[msg.sender]);
        _;
    }

    // Constructor
    function BettingGame() public {
        owner = msg.sender;
    }

    // Create a new game
    function createGame(uint _id, string _home, string _away, uint _startTime) onlyAdmin public {
        game = Game(_id, _home, _away, _startTime, false, false, Result.UNKNOWN, "");
    }

    function currentTime() public view returns(uint32) {
        return uint32(block.timestamp);
    }

    function calculateWinValue(BetContent _content, uint _odd, uint _value) internal returns (uint) {
        if (_content == BetContent.HOMEWIN || _content == BetContent.AWAYWIN || _content == BetContent.DRAW) {
            // Backs
            return _value.mul(_odd.sub(100)).div(100);
            // return _value * (_odd-100) / 100;
        }
        else {
            // Lays
            return _value.mul(100).div(_odd.sub(100));
            // return _value * 100 / (_odd-100);
        }
    }

    function isOddNew(uint8 _content, uint _odd) internal view returns(bool) {
        for (uint i=0; i<allOdds[_content].length; i++) {
            if (allOdds[_content][i] == _odd) { return false; }
        }
        return true;
    }

    // Participants place the bets
    function placeBet(BetContent _content, uint _odd, address _referrer) public payable {
        require(!game.isFreezing, "Game is frozen!");
        require(currentTime() < game.startTime, "The game has already started!");
        require(_odd>100 && _odd < 1000, "Odd should be within the range of [101, 999]!");
        require(msg.value >= 1 finney && msg.value % 1000 == 0, "Bet value should be bigger than 1 finney!");
        require(_content >= BetContent.HOMEWIN && _content <= BetContent.NOTDRAW, "Invalid betting content!");
        require(msg.sender != _referrer, "The bet placer and referrer cannot be the same!");

        // Check if it can be matched
        uint matched;
        if (liquidities[uint8(_content)][_odd] > 0) {
            matched = 0;
            liquidities[uint8(_content)][_odd] = liquidities[uint8(_content)][_odd].add(msg.value);
            emit LiquidityChangeEvent(uint8(_content), _odd, liquidities[uint8(_content)][_odd]);
        }
        else {
            // Update the bets in the opposite pool
            uint counter = calculateWinValue(_content, _odd, msg.value);
            uint8 opposite = uint8(getCounterBetContent(_content));
            bool oppositeLiquidityChanged = false;
            for (uint i=0; i < oddPools[opposite][_odd].length && counter > 0; i++) {
                Bet storage iterBet = placedBets[oddPools[opposite][_odd][i]];
                if (iterBet.cancelled) {
                    // Skip this bet if it is cancelled
                    continue;
                }
                if (iterBet.matched < iterBet.stake) {
                    if (iterBet.matched.add(counter) < iterBet.stake) {
                        // The iterBet bet is big enough
                        iterBet.matched = iterBet.matched.add(counter);
                        liquidities[opposite][_odd] = liquidities[opposite][_odd].sub(counter);
                        oppositeLiquidityChanged = true;
                        counter = 0;
                    }
                    else {
                        // Able to fill the iterBet bet
                        uint gap = iterBet.stake.sub(iterBet.matched);
                        iterBet.matched = iterBet.matched.add(gap);
                        liquidities[opposite][_odd] = liquidities[opposite][_odd].sub(gap);
                        oppositeLiquidityChanged = true;
                        counter = counter.sub(gap);
                    }
                }
            }
            if (oppositeLiquidityChanged) {
                emit LiquidityChangeEvent(opposite, _odd, liquidities[opposite][_odd]);
            }
            // Update the current bet
            if (counter > 0) {
                // Map the leftover back
                matched = calculateWinValue(BetContent(opposite), _odd, calculateWinValue(_content, _odd, msg.value).sub(counter));
                liquidities[uint8(_content)][_odd] = liquidities[uint8(_content)][_odd].add( msg.value.sub(matched) );
                emit LiquidityChangeEvent(uint8(_content), _odd, liquidities[uint8(_content)][_odd]);
            }
            else {
                // Used up
                matched = msg.value;
            }
        }

        // Bet memory b = Bet(msg.sender, msg.value, _content, _odd, betID, matched, false);
        placedBets.push(Bet(msg.sender, msg.value, _content, _odd, betID, matched, false, 0, _referrer));
        oddPools[uint8(_content)][_odd].push(betID);
        if (isOddNew(uint8(_content), _odd)) {
            allOdds[uint8(_content)].push(_odd);
        }
        userBets[msg.sender].push(betID);

        emit BetPlaceEvent(msg.sender);
        if (matched > 0) {
            emit BetMatchEvent(betID);
        }

        betID += 1; // use the index as the ID
    }

    // Participants can cancel his/her bets with the cost of certain cancel fee
    function cancelBet(uint _betID) public {
        require(!game.isFreezing, "Game is frozen!");
        require(currentTime() < game.startTime, "The game has already started!");
        require(_betID < betID, "Bet ID invalid!");
        require(msg.sender == placedBets[_betID].sender, "You have no right to call this function!");
        require(!placedBets[_betID].cancelled, "The bet is already cancelled!");
        require(placedBets[_betID].matched==0, "The bet is already matched!");
        require(liquidities[uint8(placedBets[_betID].content)][placedBets[_betID].odd].sub(placedBets[_betID].stake)>=0, "Something is wrong!");

        placedBets[_betID].cancelled = true;
        uint8 content = uint8(placedBets[_betID].content);
        uint odd = placedBets[_betID].odd;
        liquidities[content][odd] = liquidities[content][odd].sub(placedBets[_betID].stake);
        emit LiquidityChangeEvent(content, odd, liquidities[content][odd]);
        uint cancelFeeRatio = 5; // 0.5%
        uint amount = placedBets[_betID].stake.mul(1000-cancelFeeRatio).div(1000);
        uint fee = placedBets[_betID].stake.sub(amount);

        if (amount > 0) {
            placedBets[_betID].stake = 0;
            pendingWithdrawals[placedBets[_betID].sender] = pendingWithdrawals[placedBets[_betID].sender].add(amount);
            pendingWithdrawals[owner] = pendingWithdrawals[owner].add(fee);
        }

        emit BetCancelEvent(_betID);
    }

    function getCounterBetContent(BetContent _content) internal returns(BetContent) {
        BetContent newContent;

        if (_content == BetContent.HOMEWIN) { newContent=BetContent.HOMENOTWIN; }
        else if (_content == BetContent.HOMENOTWIN) { newContent=BetContent.HOMEWIN; }
        else if (_content == BetContent.AWAYWIN) { newContent=BetContent.AWAYNOTWIN; }
        else if (_content == BetContent.AWAYNOTWIN) { newContent=BetContent.AWAYWIN; }
        else if (_content == BetContent.DRAW) { newContent=BetContent.NOTDRAW; }
        else if (_content == BetContent.NOTDRAW) { newContent=BetContent.DRAW; }
        else revert("Invalid argument!");

        return newContent;
    }

    // Set the game result 
    function setResultByHand(uint8 _content, string _details) onlyAdmin public {
        require(currentTime() > game.startTime, "The game is not started yet!");

        game.ended = true;

        // Caculate winners
        if (_content == 0) { game.result = Result.HOMEWIN; }
        else if (_content == 1) { game.result = Result.AWAYWIN; }
        else if (_content == 2) { game.result = Result.DRAW; }
        else revert("No such result!");

        game.details = _details;
    }

    function checkBetWinOrLose(BetContent _content) internal returns (bool) {
        if (game.result == Result.HOMEWIN) {
            if (_content == BetContent.HOMEWIN || _content == BetContent.AWAYNOTWIN || _content == BetContent.NOTDRAW) { return true; }
            else { return false; }
        }
        else if (game.result == Result.AWAYWIN) {
            if (_content == BetContent.HOMENOTWIN || _content == BetContent.AWAYWIN || _content == BetContent.NOTDRAW) { return true; }
            else { return false; }
        }
        else if (game.result == Result.DRAW) {
            if (_content == BetContent.HOMENOTWIN || _content == BetContent.AWAYNOTWIN || _content == BetContent.DRAW) { return true; }
            else { return false; }
        }
        else revert("Invalid game result!");
    }

    function calculateWinners() onlyAdmin public {
        require(game.ended, "The game is not ended yet!");

        for (uint i=0; i<placedBets.length; i++) {
            if(placedBets[i].cancelled) {
                continue;
            }
            if (placedBets[i].matched>0) {
                uint result;
                if (checkBetWinOrLose(placedBets[i].content)) {
                    // win, return the stake + win
                    uint totalWin = calculateWinValue(placedBets[i].content, placedBets[i].odd, placedBets[i].matched);
                    uint feeRatio;
                    uint referRatio;
                    uint profit;
                    uint fee;
                    if (placedBets[i].referrer != 0x0) {
                        // somebody referred
                        feeRatio = 10; // 1%
                        referRatio = 5; // 0.5%
                        profit = totalWin.mul(1000-feeRatio-referRatio).div(1000);
                        uint referProfit = totalWin.mul(referRatio).div(1000);
                        fee = totalWin.sub(profit).sub(referProfit);
                        pendingWithdrawals[placedBets[i].referrer] = pendingWithdrawals[placedBets[i].referrer].add(referProfit);
                    }
                    else {
                        feeRatio = 20; // 2%
                        profit = totalWin.mul(1000-feeRatio).div(1000);
                        fee = totalWin.sub(profit);
                    }

                    // placedBets[i].result = placedBets[i].stake.add(profit);
                    // pendingWithdrawals[placedBets[i].sender] = pendingWithdrawals[placedBets[i].sender].add(placedBets[i].result);
                    result = placedBets[i].stake.add(profit);
                    pendingWithdrawals[placedBets[i].sender] = pendingWithdrawals[placedBets[i].sender].add(result);
                    pendingWithdrawals[owner] = pendingWithdrawals[owner].add(fee);
                }
                else {
                    // lose, return only the unmatched
                    // placedBets[i].result = placedBets[i].stake.sub(placedBets[i].matched);
                    // pendingWithdrawals[placedBets[i].sender] = pendingWithdrawals[placedBets[i].sender].add(placedBets[i].result);
                    result = placedBets[i].stake.sub(placedBets[i].matched);
                    pendingWithdrawals[placedBets[i].sender] = pendingWithdrawals[placedBets[i].sender].add(result);
                }
                placedBets[i].result = result;
            }
            else {
                uint amount = placedBets[i].stake;
                pendingWithdrawals[placedBets[i].sender] = pendingWithdrawals[placedBets[i].sender].add(amount);
            }
        }

        emit GameEndEvent(game.details);
    }

    function withdraw() public {
        require(!game.isFreezing, "Game is frozen!");
        uint amount = pendingWithdrawals[msg.sender];
        require(amount != 0);
        require(this.balance >= amount);

        pendingWithdrawals[msg.sender] = 0;
        msg.sender.transfer(amount);
    }

    // Setters
    function freezeGame(bool _freeze) onlyAdmin public {
        game.isFreezing = _freeze;
    }

    // Getters
    function getNumUserBets(address _user) public view returns(uint) {
        return userBets[_user].length;
    }

    function getUserBetID(address _user, uint _index) public view returns(uint) {
        require(userBets[_user].length>0 && userBets[_user].length>_index, "Index out of range!");

        return userBets[_user][_index];
    }

    function getNumOdds(BetContent _content) public view returns(uint) {
        return allOdds[uint8(_content)].length;
    }

    function getLiquidity(BetContent _content, uint _odd) public view returns(uint) {
        return liquidities[uint8(_content)][_odd];
    }
}
